// src/components/Skills.js
import { Box, Flex, Heading, VStack, Text, useColorMode } from '@chakra-ui/react';
import { Icon } from '@chakra-ui/react';
import { FaHtml5, FaCss3Alt, FaJs, FaReact } from 'react-icons/fa';

const Skills = () => {
  const { colorMode } = useColorMode();

  return (
    <Box id="skills" py="10" px="5" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} color={colorMode === 'light' ? 'gray.800' : 'gray.200'} borderBottom="2px solid" borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}>
      <Flex direction="column" align="center" justify="center" maxW="900px" mx="auto">
        <Heading size="xl" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} mb="8">
          My Skills
        </Heading>
        <VStack spacing="6">
          <Flex align="center" spacing="4">
            <Icon as={FaHtml5} w={12} h={12} color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
            <Text fontSize="lg" fontWeight="bold">HTML5</Text>
          </Flex>
          <Flex align="center" spacing="4">
            <Icon as={FaCss3Alt} w={12} h={12} color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
            <Text fontSize="lg" fontWeight="bold">CSS3</Text>
          </Flex>
          <Flex align="center" spacing="4">
            <Icon as={FaJs} w={12} h={12} color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
            <Text fontSize="lg" fontWeight="bold">JavaScript</Text>
          </Flex>
          <Flex align="center" spacing="4">
            <Icon as={FaReact} w={12} h={12} color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
            <Text fontSize="lg" fontWeight="bold">React</Text>
          </Flex>
        </VStack>
      </Flex>
    </Box>
  );
};

export default Skills;
