// src/components/Hero.js
import { Box, Heading, Text, Button, useColorMode } from '@chakra-ui/react';

const Hero = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'grey.900';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box bg={bgColor} minH="60vh" display="flex" flexDirection="column" justifyContent="center" alignItems="center" textAlign="center" borderBottom="2px solid" borderColor={textColor}>
      <Heading size="2xl" color={textColor}>
        Welcome to My Portfolio
      </Heading>
      <Text fontSize="xl" mt="4" color={textColor}>
        I am a Frontend Developer passionate about building beautiful web experiences.
      </Text>
      <Button
        mt="6"
        size="lg"
        bg={textColor}
        color={bgColor}
        _hover={{ bg: colorMode === 'light' ? 'black' : 'white', color: colorMode === 'light' ? 'white' : 'black' }}
      >
        View My Work
      </Button>
    </Box>
  );
};

export default Hero;
