import { Box, Heading, SimpleGrid, Text, Button, Image, Stack } from '@chakra-ui/react';

const projects = [
  {
    title: 'Project One',
    description: 'A brief description of Project One.',
    image: 'https://via.placeholder.com/400x300',
    link: '#',
  },
  {
    title: 'Project Two',
    description: 'A brief description of Project Two.',
    image: 'https://via.placeholder.com/400x300',
    link: '#',
  },
  // Add more projects as needed
];

const Projects = () => (
  <Box id="projects" py="10" px="5" bg="blackAlpha.800" borderTop="2px solid" borderColor="orange.500">
    <Heading textAlign="center" mb="8" color="orange.500">Projects</Heading>
    <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing="10" maxW="1200px" mx="auto">
      {projects.map((project, i) => (
        <Box key={i} borderWidth="1px" borderRadius="lg" overflow="hidden">
          <Image src={project.image} alt={project.title} />
          <Box p="6">
            <Heading size="md" mb="3">{project.title}</Heading>
            <Text mb="4">{project.description}</Text>
            <Button colorScheme="orange" as="a" href={project.link} target="_blank">
              View Project
            </Button>
          </Box>
        </Box>
      ))}
    </SimpleGrid>
  </Box>
);

export default Projects;
