// src/components/Skills.js
import { Box, Heading, SimpleGrid, Text, useColorMode } from '@chakra-ui/react';
import { FaHtml5, FaCss3Alt, FaJsSquare, FaReact } from 'react-icons/fa';

const skills = [
  { name: 'HTML5', icon: FaHtml5 },
  { name: 'CSS3', icon: FaCss3Alt },
  { name: 'JavaScript', icon: FaJsSquare },
  { name: 'React', icon: FaReact },
];

const Skills = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'black';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box id="skills" py="10" px="5" bg={bgColor} borderTop="2">
      <Heading textAlign="center" mb="8" color={textColor}>
        My Skills
      </Heading>

      <SimpleGrid columns={{ base: 1, sm: 2, md: 4 }} spacing="10" maxW="1200px" mx="auto">
        {skills.map((skill, index) => (
          <Box
            key={index}
            textAlign="center"
            p="6"
            bg={bgColor}
            border="1px"
            borderColor={textColor}
            borderRadius="lg"
            _hover={{ boxShadow: 'md', transform: 'scale(1.05)', transition: 'all 0.3s ease' }}
          >
            <skill.icon size="3rem" color={textColor} />
            <Text mt="4" fontSize="lg" fontWeight="bold" color={textColor}>
              {skill.name}
            </Text>
          </Box>
        ))}
      </SimpleGrid>
    </Box>
  );
};

export default Skills;
