// src/components/About.js
import { Box, Flex, Heading, Text, VStack, useColorMode } from '@chakra-ui/react';

const About = () => {
  const { colorMode } = useColorMode();

  return (
    <Box id="about" py="10" px="5" bg={colorMode === 'light' ? 'gray.900' : 'gray.800'} borderTop="2px solid" borderColor={colorMode === 'light' ? 'white' : 'gray.200'}>
      <Flex
        direction={{ base: 'column', md: 'column' }}
        align="center"
        justify="center"
        maxW="900px"
        mx="auto"
        gap="8"
      >
        {/* About Text Section */}
        <VStack align={{ base: 'center', md: 'center' }} textAlign={{ base: 'center', md: 'center' }} spacing="5">
          <Heading size="xl" color="orange.500">About Me</Heading>
          <Text fontSize="lg" color="gray.600" maxW="700px">
            Hi, I'm Kenneth, a dedicated front-end web developer passionate about creating engaging, responsive, and user-friendly digital experiences. My expertise lies in using modern front-end technologies such as React.js, HTML5, CSS3, and JavaScript to bring innovative designs to life.
          </Text>
          <Text fontSize="lg" color="gray.600" maxW="700px">
            As a lifelong learner, I continuously seek out opportunities to grow and expand my skillset.
          </Text>
          <Text fontSize="lg" color="gray.600" maxW="700px">
            I’m always excited about working on projects that challenge me creatively and technically. Let’s connect and work together to build something amazing!
          </Text>
        </VStack>
      </Flex>
    </Box>
  );
};

export default About;
