// src/components/Contact.js
import { Box, Heading, FormControl, FormLabel, Input, Textarea, Button, useColorMode } from '@chakra-ui/react';

const Contact = () => {
  const { colorMode } = useColorMode();

  return (
    <Box id="contact" py="10" px="5" bg={colorMode === 'light' ? 'gray.900' : 'gray.800'} borderTop="2px solid" borderColor={colorMode === 'light' ? 'white' : 'gray.200'}>
      <Heading textAlign="center" mb="8" color="orange.500">
        Contact Me
      </Heading>

      <Box maxW="600px" mx="auto">
        <FormControl id="name" mb="4">
          <FormLabel color={colorMode === 'light' ? 'white' : 'gray.200'}>Full Name</FormLabel>
          <Input type="text" placeholder="Enter your full name" bg={colorMode === 'light' ? 'gray.800' : 'gray.600'} borderColor="gray.600" color="white" />
        </FormControl>

        <FormControl id="email" mb="4">
          <FormLabel color={colorMode === 'light' ? 'white' : 'gray.200'}>Email Address</FormLabel>
          <Input type="email" placeholder="Enter your email" bg={colorMode === 'light' ? 'gray.800' : 'gray.600'} borderColor="gray.600" color="white" />
        </FormControl>

        <FormControl id="message" mb="4">
          <FormLabel color={colorMode === 'light' ? 'white' : 'gray.200'}>Your Message</FormLabel>
          <Textarea placeholder="Enter your message" bg={colorMode === 'light' ? 'gray.800' : 'gray.600'} borderColor="gray.600" color="white" />
        </FormControl>

        <Button colorScheme="orange" size="lg" w="full">
          Send Message
        </Button>
      </Box>
    </Box>
  );
};

export default Contact;
