// src/components/Skills.js
import { Box, Heading, SimpleGrid, Progress, Text, VStack, HStack, Icon } from '@chakra-ui/react';
import { FaReact, FaNodeJs, FaDatabase, FaGitAlt, FaCss3Alt, FaJsSquare, FaHtml5 } from 'react-icons/fa';

const skills = [
  {
    category: 'Front-End',
    skills: [
      { name: 'React.js', level: 90, icon: FaReact },
      { name: 'JavaScript', level: 85, icon: FaJsSquare },
      { name: 'HTML5', level: 95, icon: FaHtml5 },
      { name: 'CSS3', level: 90, icon: FaCss3Alt },
    ],
  },
  {
    category: 'Back-End',
    skills: [
      { name: 'Node.js', level: 80, icon: FaNodeJs },
      { name: 'Databases', level: 75, icon: FaDatabase },
    ],
  },
  {
    category: 'Tools & Platforms',
    skills: [
      { name: 'Git & Version Control', level: 85, icon: FaGitAlt },
    ],
  },
];

const Skills = () => (
  <Box id="skills" py="10" px="5" bg="gray.50" borderTop="2px solid" borderColor="gray.200">
    <Heading textAlign="center" mb="8" color="orange.500">
      My Skills
    </Heading>

    {/* Skill Categories and Levels */}
    <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing="10" maxW="1200px" mx="auto">
      {skills.map((category, index) => (
        <VStack key={index} align="start" spacing="5">
          <Heading size="md" color="gray.700">{category.category}</Heading>
          {category.skills.map((skill, i) => (
            <Box key={i} w="full" p="4" border="1px" borderColor="gray.200" borderRadius="lg" _hover={{ boxShadow: 'md', transform: 'scale(1.02)', transition: 'all 0.3s ease' }}>
              <HStack justify="space-between">
                <HStack>
                  <Icon as={skill.icon} boxSize="6" color="orange.400" />
                  <Text fontWeight="bold" fontSize="lg" color="gray.700">{skill.name}</Text>
                </HStack>
                <Text fontSize="sm" color="gray.500">{skill.level}%</Text>
              </HStack>
              <Progress value={skill.level} size="sm" colorScheme="orange" mt="3" />
            </Box>
          ))}
        </VStack>
      ))}
    </SimpleGrid>
  </Box>
);

export default Skills;
