// src/components/About.js
import { Box, Text, Heading } from '@chakra-ui/react';

const About = () => (
  <Box id="about" py="10" px="5" bg="black.50">
    <Heading textAlign="center">About Me</Heading>
    <Text mt="4" maxW="800px" mx="auto">
      I'm a web developer with a passion for creating functional and aesthetically pleasing digital experiences. I specialize in front-end technologies like React, HTML5, and CSS3, with an emphasis on delivering responsive, user-friendly designs.
    </Text>
  </Box>
);
export default About;
