// src/components/Footer.js
import { Box, Flex, Text, useColorMode, Link } from '@chakra-ui/react';
import { FaGithub, FaLinkedin, FaTwitter } from 'react-icons/fa';

const Footer = () => {
  const { colorMode } = useColorMode();

  return (
    <Box
      py="6"
      px="5"
      bg={colorMode === 'light' ? 'gray.900' : 'gray.800'}
      color={colorMode === 'light' ? 'white' : 'gray.200'}
      borderTop="2px solid"
      borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}
    >
      <Flex direction="column" align="center" justify="center" maxW="1200px" mx="auto">
        <Text mb="4">
          Â© {new Date().getFullYear()} Kenneth. All rights reserved.
        </Text>
        <Flex spacing="4">
          <Link href="https://github.com/yourusername" isExternal>
            <FaGithub size="24px" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
          </Link>
          <Link href="https://linkedin.com/in/yourusername" isExternal>
            <FaLinkedin size="24px" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
          </Link>
          <Link href="https://twitter.com/yourusername" isExternal>
            <FaTwitter size="24px" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
          </Link>
        </Flex>
      </Flex>
    </Box>
  );
};

export default Footer;
