// src/components/Skills.js
import { Box, Heading, SimpleGrid, Progress, Text, VStack, HStack, Icon } from '@chakra-ui/react';
import { FaReact, FaCss3Alt, FaJsSquare, FaHtml5, FaGitAlt } from 'react-icons/fa';

const frontendSkills = [
  { name: 'React.js', level: 90, icon: FaReact },
  { name: 'JavaScript', level: 80, icon: FaJsSquare },
  { name: 'HTML5', level: 95, icon: FaHtml5 },
  { name: 'CSS3', level: 90, icon: FaCss3Alt },
  { name: 'Git & Version Control', level: 85, icon: FaGitAlt },
];

const Skills = () => (
  <Box id="skills" py="10" px="5" bg="black.100" borderTop="2px solid" borderColor="">
    <Heading textAlign="center" mb="8" color="orange.500">
     Skills
    </Heading>

    {/* Skill Cards */}
    <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing="10" maxW="1200px" mx="auto">
      {frontendSkills.map((skill, i) => (
        <Box
          key={i}
          w="full"
          p="4"
          border="1px"
          borderColor="black.100"
          borderRadius="lg"
          _hover={{ boxShadow: 'md', transform: 'scale(1.02)', transition: 'all 0.3s ease' }}
        >
          <HStack justify="space-between">
            <HStack>
              <Icon as={skill.icon} boxSize="6" color="orange.400" />
              <Text fontWeight="bold" fontSize="lg" color="white">{skill.name}</Text>
            </HStack>
            <Text fontSize="sm" color="white">{skill.level}%</Text>
          </HStack>
          <Progress value={skill.level} size="sm" colorScheme="orange" mt="3" />
        </Box>
      ))}
    </SimpleGrid>
  </Box>
);

export default Skills;
