// src/components/Hero.js
import { Box, Heading, Text, Button, useColorMode, Flex, Image } from '@chakra-ui/react';
import SEO from './SEO'; // Import SEO component

const Hero = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'grey.900';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box bg={bgColor} minH="60vh" display="flex" flexDirection={{ base: 'column', md: 'row' }} justifyContent="center" alignItems="center" textAlign="center" borderBottom="2px solid" borderColor={textColor}>
      <SEO 
        title="Kenneth's Portfolio - Hero" 
        description="Welcome to Kenneth's portfolio. Discover his skills and projects." 
        keywords="frontend developer, portfolio, React, web design" 
      />
      <Box flex="1" p="4">
        <Heading size="2xl" color={textColor}>
          Hello There
        </Heading>
        <Heading size="xl" color={textColor} mt="2">
          Kenneth
        </Heading>
        <Text fontSize="xl" mt="4" color={textColor}>
          I am a Frontend Developer passionate about building beautiful web experiences.
        </Text>
        <Button
          mt="6"
          size="lg"
          bg={textColor}
          color={bgColor}
          _hover={{ bg: colorMode === 'light' ? 'black' : 'white', color: colorMode === 'light' ? 'white' : 'black' }}
        >
          View My Work
        </Button>
      </Box>
      <Box flex="1" display="flex" justifyContent="center" alignItems="center">
        <Image src="https://via.placeholder.com/400" alt="Hero" boxSize="100%" loading="lazy" />
      </Box>
    </Box>
  );
};

export default Hero;
