// src/components/About.js
import { Box, Flex, Heading, Text, useColorMode } from '@chakra-ui/react';

const About = () => {
  const { colorMode } = useColorMode();

  return (
    <Box id="about" py="10" px="5" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} color={colorMode === 'light' ? 'gray.800' : 'gray.200'} borderBottom="2px solid" borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}>
      <Flex direction="column" align="center" justify="center" maxW="900px" mx="auto">
        <Heading size="xl" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} mb="8">
          About Me
        </Heading>
        <Text fontSize="lg" lineHeight="1.6">
          Hello! I'm Kenneth, a passionate front-end developer with a knack for creating visually appealing and user-friendly interfaces. With a background in web development and UI/UX design, I thrive on transforming ideas into functional and engaging web experiences.
          <br />
          <br />
          My journey in tech started with a fascination for coding and design, and it has since evolved into a fulfilling career where I enjoy solving complex problems and continuously learning new technologies. Whether working on a team project or tackling challenges independently, I am dedicated to delivering high-quality results and pushing the boundaries of web development.
          <br />
          <br />
          Feel free to explore my portfolio to see some of the projects I've worked on and learn more about my skills and experiences. I'm always excited to connect with fellow tech enthusiasts and explore new opportunities!
        </Text>
      </Flex>
    </Box>
  );
};

export default About;
