import { Box, Heading, Text, Button, Flex, Image, useColorMode } from '@chakra-ui/react';

const Hero = () => {
  const { colorMode } = useColorMode();
  
  // Define colors
  const bgColor = colorMode === 'light' ? 'gray.200' : 'gray.900';
  const textColor = colorMode === 'light' ? 'gray.900' : 'gray.200';
  
  return (
    <Box
      bg={bgColor}
      minH="60vh"
      borderBottom="2px solid"
      borderColor={textColor}
      display="flex"
      alignItems="center"
      justifyContent="center"
      px={8}
      transition="background-color 0.2s, color 0.2s"
    >
      <Flex
        direction={{ base: "column", md: "row" }}
        alignItems="center"
        justifyContent="space-between"
        width="100%"
        maxW="1200px"
        textAlign={{ base: "center", md: "left" }}
      >
        {/* Left Section with Text */}
        <Box flex="1" mr={{ md: 8 }}>
          <Heading size="2xl" color={textColor}>
            Welcome to My Portfolio
          </Heading>
          <Text fontSize="xl" mt="4" color={textColor}>
            I am a Frontend Developer passionate about building beautiful web experiences.
          </Text>
          <Button
            mt="6"
            size="lg"
            bg={textColor}
            color={bgColor}
            _hover={{
              bg: colorMode === 'light' ? 'gray.900' : 'gray.200',
              color: colorMode === 'light' ? 'gray.200' : 'gray.900',
            }}
          >
            View My Work
          </Button>
        </Box>

        {/* Right Section with Image */}
        <Box flex="1" mt={{ base: 8, md: 0 }}>
          <Image
            src="https://via.placeholder.com/400" // Replace with actual image URL
            alt="Portfolio showcase"
            borderRadius="md"
            boxShadow="lg"
            maxW="100%"
          />
        </Box>
      </Flex>
    </Box>
  );
};

export default Hero;
