// src/components/Footer.js
import { Box, Text, HStack, Icon, Link, useColorMode } from '@chakra-ui/react';
import { FaLinkedin, FaGithub, FaTwitter } from 'react-icons/fa';

const Footer = () => {
  const { colorMode } = useColorMode();

  return (
    <Box py="4" bg={colorMode === 'light' ? 'gray.900' : 'gray.800'} borderTop="2px solid" borderColor={colorMode === 'light' ? 'white' : 'g'}>
      <HStack justify="center" spacing="6" mb="4">
        <Link href="https://linkedin.com" isExternal>
          <Icon as={FaLinkedin} boxSize="6" color="orange.400" />
        </Link>
        <Link href="https://github.com" isExternal>
          <Icon as={FaGithub} boxSize="6" color="orange.400" />
        </Link>
        <Link href="https://twitter.com" isExternal>
          <Icon as={FaTwitter} boxSize="6" color="orange.400" />
        </Link>
      </HStack>

      <Text textAlign="center" color={colorMode === 'light' ? 'white' : 'gray.200'}>
        &copy; 2024 Kenneth's Portfolio. All Rights Reserved.
      </Text>
    </Box>
  );
};

export default Footer;
