// src/components/Header.js
import { Box, Flex, Heading, HStack, Link, Text, Icon, useBreakpointValue } from '@chakra-ui/react';
import { FaHome, FaUser, FaCode, FaProjectDiagram, FaEnvelope } from 'react-icons/fa';

const Header = () => {
  const fontSize = useBreakpointValue({ base: 'lg', md: 'xl' });

  return (
    <Box bg="gray.900" color="white" py="4" px="5" borderBottom="2px solid" borderColor="orange.500">
      <Flex direction="row" align="center" justify="space-between" maxW="1200px" mx="auto">
        {/* Logo and Branding */}
        <Heading
          size="lg"
          fontFamily="'Roboto', sans-serif"
          color="orange.400"
        >
          Kenneth's Portfolio
        </Heading>

        {/* Navigation Menu */}
        <HStack spacing="8" fontSize={fontSize}>
          <Link href="#home" _hover={{ color: 'orange.300' }} display="flex" alignItems="center">
            <Icon as={FaHome} mr="2" /> Home
          </Link>
          <Link href="#about" _hover={{ color: 'orange.300' }} display="flex" alignItems="center">
            <Icon as={FaUser} mr="2" /> About
          </Link>
          <Link href="#skills" _hover={{ color: 'orange.300' }} display="flex" alignItems="center">
            <Icon as={FaCode} mr="2" /> Skills
          </Link>
          <Link href="#projects" _hover={{ color: 'orange.300' }} display="flex" alignItems="center">
            <Icon as={FaProjectDiagram} mr="2" /> Projects
          </Link>
          <Link href="#contact" _hover={{ color: 'orange.300' }} display="flex" alignItems="center">
            <Icon as={FaEnvelope} mr="2" /> Contact
          </Link>
        </HStack>
      </Flex>
    </Box>
  );
};

export default Header;
