// src/components/Contact.js
import { Box, Flex, Heading, Text, Input, Textarea, Button, useColorMode } from '@chakra-ui/react';

const Contact = () => {
  const { colorMode } = useColorMode();

  return (
    <Box id="contact" py="10" px="5" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} color={colorMode === 'light' ? 'gray.800' : 'gray.200'} borderBottom="2px solid" borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}>
      <Flex direction="column" align="center" justify="center" maxW="900px" mx="auto">
        <Heading size="xl" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} mb="8">
          Contact Me
        </Heading>
        <Text fontSize="lg" mb="6">
          I'd love to hear from you! Whether you have a question, feedback, or just want to connect, feel free to drop me a message using the form below.
        </Text>
        <Box as="form" width="100%">
          <Input
            placeholder="Your Name"
            mb="4"
            bg={colorMode === 'light' ? 'white' : 'gray.800'}
            color={colorMode === 'light' ? 'gray.800' : 'gray.200'}
            borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}
            _placeholder={{ color: colorMode === 'light' ? 'gray.600' : 'gray.400' }}
          />
          <Input
            type="email"
            placeholder="Your Email"
            mb="4"
            bg={colorMode === 'light' ? 'white' : 'gray.800'}
            color={colorMode === 'light' ? 'gray.800' : 'gray.200'}
            borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}
            _placeholder={{ color: colorMode === 'light' ? 'gray.600' : 'gray.400' }}
          />
          <Textarea
            placeholder="Your Message"
            mb="4"
            bg={colorMode === 'light' ? 'white' : 'gray.800'}
            color={colorMode === 'light' ? 'gray.800' : 'gray.200'}
            borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}
            _placeholder={{ color: colorMode === 'light' ? 'gray.600' : 'gray.400' }}
          />
          <Button
            type="submit"
            colorScheme="orange"
            size="lg"
          >
            Send Message
          </Button>
        </Box>
      </Flex>
    </Box>
  );
};

export default Contact;
