// src/components/Contact.js
import { Box, Heading, FormControl, FormLabel, Input, Textarea, Button, useColorMode } from '@chakra-ui/react';

const Contact = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'grey.900';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box id="contact" py="10" px="5" bg={bgColor} borderTop="2px solid" borderColor={textColor}>
      <Heading textAlign="center" mb="8" color={textColor}>
        Contact Me
      </Heading>

      <Box maxW="600px" mx="auto">
        <FormControl id="name" mb="4">
          <FormLabel color={textColor}>Full Name</FormLabel>
          <Input type="text" placeholder="Enter your full name" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} borderColor={textColor} color={textColor} />
        </FormControl>

        <FormControl id="email" mb="4">
          <FormLabel color={textColor}>Email Address</FormLabel>
          <Input type="email" placeholder="Enter your email" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} borderColor={textColor} color={textColor} />
        </FormControl>

        <FormControl id="message" mb="4">
          <FormLabel color={textColor}>Your Message</FormLabel>
          <Textarea placeholder="Enter your message" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} borderColor={textColor} color={textColor} />
        </FormControl>

        <Button bg={textColor} color={bgColor} size="lg" w="full" _hover={{ bg: colorMode === 'light' ? 'black' : 'white', color: colorMode === 'light' ? 'white' : 'black' }}>
          Send Message
        </Button>
      </Box>
    </Box>
  );
};

export default Contact;
