import { Box, Heading, FormControl, FormLabel, Input, Textarea, Button, useBreakpointValue } from '@chakra-ui/react';

const Contact = () => {
  const formWidth = useBreakpointValue({ base: '100%', md: '70%', lg: '50%' });

  return (
    <Box id="contact" py="10" px="5" bg="blackAlpha.800" borderTop="2px solid" borderColor="orange.500">
      <Heading textAlign="center" mb="8" color="orange.500">Contact</Heading>
      <Box maxW="1200px" mx="auto">
        <FormControl id="contact-form" w={formWidth} mx="auto">
          <FormLabel htmlFor="name" color="white">Name</FormLabel>
          <Input type="text" id="name" placeholder="Your Name" mb="4" />
          <FormLabel htmlFor="email" color="white">Email</FormLabel>
          <Input type="email" id="email" placeholder="Your Email" mb="4" />
          <FormLabel htmlFor="message" color="white">Message</FormLabel>
          <Textarea id="message" placeholder="Your Message" mb="4" />
          <Button colorScheme="orange" type="submit">Send Message</Button>
        </FormControl>
      </Box>
    </Box>
  );
};

export default Contact;
