// src/components/Hero.js
import { Box, Heading, Text, Button, Image, Flex } from '@chakra-ui/react';

const Hero = () => (
  <Box bg="gray.50" py="10" px="5">
    <Flex direction={{ base: 'column', md: 'row' }} align="center" justify="center">
      
      {/* Image Section */}
      <Image
        src="c:\Users\hp\Downloads\FB_IMG_1683359480471.jpg"  //
        alt="Kenneth's profile"
        borderRadius="full"
        boxSize={{ base: '150px', md: '300px' }}
        objectFit="cover"
        mr={{ md: '10' }}
        mb={{ base: '6', md: '0' }}
      />
      
      {/* Text Section */}
      <Box textAlign={{ base: 'center', md: 'left' }}>
        <Heading as="h1" size="2xl">Hi, I'm Kenneth</Heading>
        <Text fontSize="lg" mt="4" color="gray.600">
          A Passionate Web Developer with experience in front-end technologies.
        </Text>
        <Button mt="6" colorScheme="orange" size="lg">Check out my Projects</Button>
      </Box>
    </Flex>
  </Box>
);

export default Hero;
