// src/components/Projects.js
import { Box, Heading, SimpleGrid, Image, Text, Link, useColorMode } from '@chakra-ui/react';

const projects = [
  {
    title: 'Cryptocurrency Tracker',
    image: 'https://via.placeholder.com/400',
    description: 'A cryptocurrency tracker app built with React and Chakra UI.',
    link: '#',
  },
  {
    title: 'Portfolio Website',
    image: 'https://via.placeholder.com/400',
    description: 'A personal portfolio website showcasing projects and skills.',
    link: '#',
  },
];

const Projects = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : '';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box id="projects" py="10" px="5" bg={bgColor} borderTop="2px solid" borderColor={textColor}>
      <Heading textAlign="center" mb="8" color={textColor}>
        Projects
      </Heading>

      <SimpleGrid columns={{ base: 1, md: 2 }} spacing="10" maxW="1200px" mx="auto">
        {projects.map((project, index) => (
          <Box
            key={index}
            border="1px"
            borderColor={textColor}
            borderRadius="lg"
            overflow="hidden"
            _hover={{ boxShadow: 'md', transform: 'scale(1.02)', transition: 'all 0.3s ease' }}
          >
            <Image src={project.image} alt={project.title} boxSize="100%" />
            <Box p="6" bg={bgColor}>
              <Heading size="md" mb="2" color={textColor}>
                {project.title}
              </Heading>
              <Text color={textColor} mb="4">
                {project.description}
              </Text>
              <Link href={project.link} color={colorMode === 'light' ? 'black' : 'white'} _hover={{ textDecoration: 'underline' }}>
                View Project
              </Link>
            </Box>
          </Box>
        ))}
      </SimpleGrid>
    </Box>
  );
};

export default Projects;
