// src/components/Contact.js
import { Box, Flex, Heading, VStack, HStack, Icon, Input, Textarea, Button, Link, Text } from '@chakra-ui/react';
import { FaLinkedin, FaGithub, FaTwitter } from 'react-icons/fa';
import { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    // Add your form handling logic here (e.g., send the data to your email or API)
  };

  return (
    <Box id="contact" py="10" px="5" bg="black.100" borderTop="2px solid" borderColor=".100">
      <Flex direction="column" align="center" justify="center" maxW="900px" mx="auto" gap="8">
        <Heading size="xl" color="orange.500" textAlign="center">
          Get in Touch
        </Heading>
        <Text fontSize="lg" color="gray.600" textAlign="center" maxW="700px">
          Whether you're interested in working together, have a project idea, or just want to connect, feel free to reach out. I'm always excited to hear from other professionals and developers.
        </Text>

        {/* Contact Form */}
        <Box as="form" w="full" maxW="700px" onSubmit={handleSubmit}>
          <VStack spacing="5">
            <Input
              name="name"
              placeholder="Your Name"
              value={formData.name}
              onChange={handleChange}
              focusBorderColor="orange.400"
              variant="outline"
              isRequired
            />
            <Input
              name="email"
              type="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={handleChange}
              focusBorderColor="orange.400"
              variant="outline"
              isRequired
            />
            <Textarea
              name="message"
              placeholder="Your Message"
              value={formData.message}
              onChange={handleChange}
              focusBorderColor="orange.400"
              variant="outline"
              isRequired
            />
            <Button
              type="submit"
              colorScheme="orange"
              size="lg"
              w="full"
              _hover={{ bg: 'orange.600' }}
            >
              Send Message
            </Button>
          </VStack>
        </Box>

        {/* Social Media Links */}
        <HStack spacing="6" pt="5">
          <Link href="https://linkedin.com/in/your-linkedin" isExternal _hover={{ color: 'orange.400' }}>
            <Icon as={FaLinkedin} w={6} h={6} />
          </Link>
          <Link href="https://github.com/your-github" isExternal _hover={{ color: 'orange.400' }}>
            <Icon as={FaGithub} w={6} h={6} />
          </Link>
          <Link href="https://twitter.com/your-twitter" isExternal _hover={{ color: 'orange.400' }}>
            <Icon as={FaTwitter} w={6} h={6} />
          </Link>
        </HStack>
      </Flex>
    </Box>
  );
};

export default Contact;
