// src/components/Footer.js
import { Box, Text, Flex, Link } from '@chakra-ui/react';
import { FaLinkedin, FaGithub, FaTwitter } from 'react-icons/fa';

const Footer = () => (
  <Box as="footer" py="6" bg="gray.900" color="white">
    <Flex direction={{ base: 'column', md: 'row' }} justify="space-between" align="center" px="5">
      
      {/* Copyright Section */}
      <Text fontSize="sm" mb={{ base: '4', md: '0' }}>
        Â© {new Date().getFullYear()} Kenneth's Portfolio. All rights reserved.
      </Text>

      {/* Social Media Links */}
      <Flex gap="4">
        <Link href="https://linkedin.com/in/your-linkedin" isExternal>
          <FaLinkedin size="20px" />
        </Link>
        <Link href="https://github.com/your-github" isExternal>
          <FaGithub size="20px" />
        </Link>
        <Link href="https://twitter.com/your-twitter" isExternal>
          <FaTwitter size="20px" />
        </Link>
      </Flex>
    </Flex>
  </Box>
);

export default Footer;
