// src/components/Projects.js
import { SimpleGrid, Box, Heading, Text, Image, Link } from '@chakra-ui/react';

const projects = [
  {
    title: "Cryptocurrency Tracker",
    description: "A React-based app for tracking crypto prices with Chart.js integration.",
    img: "link-to-image",
    link: "link-to-project"
  },
  {
    title: "Incident Response Toolkit",
    description: "A software toolkit for handling basic incident responses efficiently.",
    img: "link-to-image",
    link: "link-to-project"
  }
];

const Projects = () => (
  <Box id="projects" py="10">
    <Heading textAlign="center">My Projects</Heading>
    <SimpleGrid columns={{ base: 1, md: 2 }} spacing="10" mt="8">
      {projects.map((project, index) => (
        <Box key={index} p="5" shadow="md" borderWidth="1px">
          <Image src={project.img} alt={project.title} />
          <Heading fontSize="xl" mt="4">{project.title}</Heading>
          <Text mt="2">{project.description}</Text>
          <Link href={project.link} color="orange.400" mt="4" display="block">View Project</Link>
        </Box>
      ))}
    </SimpleGrid>
  </Box>
);
export default Projects;
