// src/components/Footer.js
import { Box, Text, HStack, Icon, Link, useColorMode } from '@chakra-ui/react';
import { FaLinkedin, FaGithub, FaTwitter } from 'react-icons/fa';
import { FaWhatsapp } from 'react-icons/fa';

const Footer = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'grey.900';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <>
      <Box py="4" bg={bgColor} borderTop="2px solid" borderColor={textColor}>
        <HStack justify="center" spacing="6" mb="4">
          <Link href="https://linkedin.com" isExternal>
            <Icon as={FaLinkedin} boxSize="6" color={textColor} />
          </Link>
          <Link href="https://github.com" isExternal>
            <Icon as={FaGithub} boxSize="6" color={textColor} />
          </Link>
          <Link href="https://twitter.com" isExternal>
            <Icon as={FaTwitter} boxSize="6" color={textColor} />
          </Link>
        </HStack>

        <Text textAlign="center" color={textColor}>
          &copy; 2024 Kenneth's Portfolio. All Rights Reserved.
        </Text>
      </Box>

      <a
        href="https://wa.me/1234567890" // Replace with your WhatsApp number
        target="_blank"
        rel="noopener noreferrer"
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          backgroundColor: '#25D366',
          color: 'white',
          borderRadius: '50%',
          padding: '10px',
          boxShadow: '0 2px 10px rgba(0, 0, 0, 0.2)',
          zIndex: '1000',
        }}
      >
        <FaWhatsapp size="24" />
      </a>
    </>
  );
};

export default Footer;
