// src/components/Projects.js
import { Box, Flex, Heading, SimpleGrid, Text, useColorMode } from '@chakra-ui/react';
import { Icon } from '@chakra-ui/react';
import { FaGithub, FaLink } from 'react-icons/fa';

const ProjectCard = ({ title, description, githubLink, demoLink }) => {
  const { colorMode } = useColorMode();

  return (
    <Box
      p="5"
      borderRadius="md"
      bg={colorMode === 'light' ? 'white' : 'gray.800'}
      boxShadow="md"
      borderWidth="1px"
      borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}
    >
      <Heading size="md" mb="2" color={colorMode === 'light' ? 'gray.800' : 'gray.200'}>
        {title}
      </Heading>
      <Text mb="4" color={colorMode === 'light' ? 'gray.600' : 'gray.400'}>
        {description}
      </Text>
      <Flex justify="space-between">
        {githubLink && (
          <a href={githubLink} target="_blank" rel="noopener noreferrer">
            <Icon as={FaGithub} boxSize="6" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
          </a>
        )}
        {demoLink && (
          <a href={demoLink} target="_blank" rel="noopener noreferrer">
            <Icon as={FaLink} boxSize="6" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} />
          </a>
        )}
      </Flex>
    </Box>
  );
};

const Projects = () => {
  const { colorMode } = useColorMode();

  return (
    <Box id="projects" py="10" px="5" bg={colorMode === 'light' ? 'gray.100' : 'gray.700'} color={colorMode === 'light' ? 'gray.800' : 'gray.200'} borderBottom="2px solid" borderColor={colorMode === 'light' ? 'orange.500' : 'orange.300'}>
      <Flex direction="column" align="center" justify="center" maxW="1200px" mx="auto">
        <Heading size="xl" color={colorMode === 'light' ? 'orange.500' : 'orange.300'} mb="8">
          My Projects
        </Heading>
        <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing="8">
          <ProjectCard
            title="Project 1"
            description="A brief description of Project 1."
            githubLink="https://github.com/yourusername/project1"
            demoLink="https://yourproject1.com"
          />
          <ProjectCard
            title="Project 2"
            description="A brief description of Project 2."
            githubLink="https://github.com/yourusername/project2"
            demoLink="https://yourproject2.com"
          />
          <ProjectCard
            title="Project 3"
            description="A brief description of Project 3."
            githubLink="https://github.com/yourusername/project3"
            demoLink="https://yourproject3.com"
          />
        </SimpleGrid>
      </Flex>
    </Box>
  );
};

export default Projects;
