// src/components/Header.js
import { Box, Flex, Heading, Spacer, Link, Button, useColorMode, IconButton } from '@chakra-ui/react';
import { FaSun, FaMoon } from 'react-icons/fa';

const Header = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'grey.';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box bg={bgColor} px="4" py="2" boxShadow="sm" borderBottom="2px solid" borderColor={textColor}>
      <Flex align="center">
        <Heading size="lg" color={textColor}>
          Kenneth's Portfolio
        </Heading>
        <Spacer />
        <Flex>
          <Link href="#about" mx="4" color={textColor} _hover={{ textDecoration: 'underline' }}>
            About
          </Link>
          <Link href="#skills" mx="4" color={textColor} _hover={{ textDecoration: 'underline' }}>
            Skills
          </Link>
          <Link href="#projects" mx="4" color={textColor} _hover={{ textDecoration: 'underline' }}>
            Projects
          </Link>
          <Link href="#contact" mx="4" color={textColor} _hover={{ textDecoration: 'underline' }}>
            Contact
          </Link>
          <IconButton
            aria-label="Toggle dark mode"
            icon={colorMode === 'light' ? <FaMoon /> : <FaSun />}
            onClick={toggleColorMode}
            bg={textColor}
            color={bgColor}
            ml="4"
            _hover={{ bg: colorMode === 'light' ? 'black' : 'white', color: colorMode === 'light' ? 'white' : 'black' }}
          />
        </Flex>
      </Flex>
    </Box>
  );
};

export default Header;
