// src/components/Projects.js
import { Box, Heading, SimpleGrid, Text, Image, Link } from '@chakra-ui/react';
import { motion } from 'framer-motion';

const MotionBox = motion(Box);

const projects = [
  {
    title: 'Crypto Tracker App',
    description: 'A React-based cryptocurrency tracker with real-time data.',
    image: 'src\Images\original-cafc685598a67641efac63efea3139b5.png',
    link: 'https://k5cryptotrackerapp.netlify.app/',
  },
  {
    title: 'Portfolio Website',
    description: 'A sleek personal portfolio to showcase my skills and projects.',
    image: 'src\Images\personal-portfolio-website.jpg', 
    link: 'https://github.com/your-portfolio',
  },
  {
    title: 'AgriVista',
    description: 'A fully responsive e-commerce website with shopping cart functionality.',
    image: 'bussrc\Images\h-hogging.jpg', 
    link: 'https://dapper-sopapillas-b3ffd8.netlify.app/',
  },
];

const Projects = () => (
  <Box id="projects" py="10" px="5" bg="gray.100">
    <Heading textAlign="center" mb="8" color="gray.700">
      My Projects
    </Heading>

    <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing="10">
      {projects.map((project, index) => (
        <MotionBox
          key={index}
          bg="white"
          borderRadius="lg"
          overflow="hidden"
          boxShadow="lg"
          transition="all 0.3s ease"
          whileHover={{ scale: 1.05, boxShadow: '2xl' }}
          whileTap={{ scale: 0.95 }}
          _hover={{ bg: 'orange.100' }}
        >
          {/* Project Image */}
          <Image
            src={project.image}
            alt={project.title}
            objectFit="cover"
            h="200px"
            w="full"
          />

          {/* Project Details */}
          <Box p="6">
            <Heading size="md" mb="3">{project.title}</Heading>
            <Text mb="3" color="gray.600">{project.description}</Text>
            <Link
              href={project.link}
              color="orange.500"
              fontWeight="bold"
              isExternal
            >
              View Project
            </Link>
          </Box>
        </MotionBox>
      ))}
    </SimpleGrid>
  </Box>
);

export default Projects;
