// src/components/About.js
import { Box, Heading, Text, useColorMode } from '@chakra-ui/react';

const About = () => {
  const { colorMode } = useColorMode();
  const bgColor = colorMode === 'light' ? 'white' : 'grey.900';
  const textColor = colorMode === 'light' ? 'black' : 'white';

  return (
    <Box id="about" py="10" px="5" bg={bgColor} borderTop="2px solid" borderColor={textColor}>
      <Heading textAlign="center" mb="8" color={textColor}>
        About Me
      </Heading>
      <Text color={textColor} maxW="800px" mx="auto" textAlign="center" fontSize="lg">
        I am a passionate Frontend Developer with experience in building responsive and user-friendly web applications.
        I enjoy solving problems and learning new technologies to continuously improve my skills.
      </Text>
    </Box>
  );
};

export default About;
