import { Box, Heading, Text, useColorMode } from '@chakra-ui/react';

const About = () => {
  const { colorMode } = useColorMode();

  // Define colors based on light/dark mode
  const bgColor = colorMode === 'light' ? 'gray.200' : 'gray.900';
  const textColor = colorMode === 'light' ? 'gray.900' : 'gray.200';
  const borderColor = colorMode === 'light' ? 'gray.400' : 'gray.600';

  return (
    <Box
      id="about"
      py="16"
      px="5"
      bg={bgColor}
      borderTop=""
      borderColor={borderColor}
      transition="background-color 0.2s, color 0.2s"
    >
      <Heading
        textAlign="center"
        mb="8"
        color={textColor}
        position="relative"
        display="inline-block"
        _hover={{
          _after: {
            width: '100%',
          },
        }}
        _after={{
          content: '""',
          position: 'absolute',
          bottom: '-6px',
          left: '50%',
          width: '60%',
          height: '2px',
          bg: borderColor,
          transition: 'width 0.4s ease',
          transform: 'translateX(-50%)',
          width: '0',
        }}
      >
        About Me
      </Heading>

      <Text
        color={textColor}
        maxW="800px"
        mx="auto"
        textAlign="center"
        fontSize="lg"
        lineHeight="1.8"
        letterSpacing="wide"
        opacity={0.85}
        px={{ base: 4, md: 0 }}
      >
        I am a passionate Frontend Developer with a keen interest in building responsive and user-friendly web applications.
        I thrive on solving complex problems and constantly seek to expand my knowledge of new technologies. My journey has been fueled by a love for elegant code and creative design.
      </Text>
    </Box>
  );
};

export default About;
